                    <CardHeader className="p-0 pb-6">
                      <CardTitle className="text-2xl font-semibold">Request a Quote</CardTitle>
                    </CardHeader>
                    <CardContent className="p-0">
                      <form onSubmit={handleQuoteSubmit} className="space-y-6">
